import React from "react";
import Form from "../components/Form";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const fields = [
  {
    label: "Full Name",
    type: "text",
  },
  {
    label: "Email Id",
    type: "text",
  },
  {
    label: "Phone Number",
    type: "number",
    name: "Phone",
  },
  {
    label: "Address",
    type: "text",
  },
  {
    label: "Password",
    type: "password",
  },
  {
    label: "Confirm Password",
    type: "password",
  },
];

const notify = (name, phone) =>
  toast.error(
    `Hello ${name}, Your number ${phone} have been successfully registered!`,
    {
      position: "bottom-center",
      autoClose: 3000,
      hideProgressBar: true,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
    }
  );

class Login extends React.Component {
  onSubmit = (event) => {
    event.preventDefault();
    console.log("=======>", event.target.elements[0].value);
    const name = event.target.elements[0].value;
    const emailId = event.target.elements[1].value;
    const phoneNumber = event.target.elements[2].value;
    const address = event.target.elements[3].value;
    const password = event.target.elements[4].value;
    const confirmedPassword = event.target.elements[5].value;

    // const password = event.target.elements[1].value;
    const message = "You are succesfully registered!";

    const requestBody = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        virtualNumber: "33644633627",
        toNumber: phoneNumber,
        message: message,
      }),
    };

    if (password === confirmedPassword) {
      fetch("http://localhost:3000/send", requestBody)
        .then((res) => res.json())
        .then((res) => {
          if (res.messages[0]["status"] === "0") {
            notify(name, phoneNumber);
          }
        });
    }
  };

  render() {
    return (
      <div>
        <Form
          fields={fields}
          onSubmit={this.onSubmit}
          buttonLabel={"Register"}
          linkTo={"/success"}
        />
        <ToastContainer />
      </div>
    );
  }
}

export default Login;
