import React from "react";
import Form from "../components/Form";
import { useNavigate } from "react-router-dom";

const fields = [
  {
    label: "Enter your 4 digit code",
    type: "number",
  },
];

const onSubmit = (event, navigate) => {
  event.preventDefault();
  const code = event.target.elements[0].value;

  const verifyRequest = {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      request_id: request_id,
      code: code,
    }),
  };

  fetch("http://localhost:3000/check", verifyRequest)
    .then((res) => res.json())
    .then((res) => this.setState({ verifyResponse: res }));

  fetch("http://localhost:3000/request", resisterRequest)
    .then((res) => res.json())
    .then((res) => {
      if (res.status === "0") {
        console.log("response is.....", res);
        this.setState({ registerResponse: res });
        navigate(`/verify/${res.request_id}`);
      }
    });
};

const Verify = () => {
  let navigate = useNavigate();
  return (
    <Form
      fields={fields}
      onSubmit={(e) => onSubmit(e, navigate)}
      buttonLabel={"Verify Me!"}
      // linkTo={"/verify"}
      // apiResponse={this.state.registerResponse}
    />
  );
};

export default Verify;

