import React, { useState } from "react";
import Form from "../components/Form";
import { useNavigate, useParams } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const fields = [
  {
    label: "Enter your 4 digit code",
    type: "number",
  },
];

const onSubmit = (event, navigate, params, notify) => {
  event.preventDefault();
  const request_id = params && params.request_id;
  const code = event.target.elements[0].value;

  console.log("request_id is....", request_id);

  const verifyRequest = {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      request_id: "3d23c30f9cdf4862bd14e5ea9ef8f5a4",
      code: code,
    }),
  };

  setFlag(true);
  notify();

  //   fetch("http://localhost:3000/check", verifyRequest)
  //     .then((res) => res.json())
  //     .then((res) => {
  //       if (res.status === "0") {
  //         console.log("verify response is.....", res);
  //         navigate("/personal-details");
  //       } else {
  //         return <Toast />;
  //       }
  //     });
};

const Verify = () => {
  const [flag, setFlag] = useState(false);
  let navigate = useNavigate();
  let params = useParams();
  const notify = () =>
    toast.error("Wow so easy!", {
      position: "bottom-center",
      autoClose: 5000,
      hideProgressBar: true,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
    });

  return (
    <div>
      <Form
        fields={fields}
        onSubmit={(e) => onSubmit(e, navigate, params, notify)}
        buttonLabel={"Verify Me!"}
      />
      {flag && <ToastContainer />}
    </div>
  );
};

export default Verify;

