import React from 'react';
import {Link} from "react-router-dom"

const formStyle = {
    // margin: 'auto',
    padding: '30px',
    border: '1px solid #c9c9c9',
    borderRadius: '5px',
    background: '#f5f5f5',
    width: '400px',
    display: 'block',
    height : '300px'
};

const labelStyle = {
    // margin: '10px 0 5px 0',
    fontFamily: 'Arial, Helvetica, sans-serif',
    fontSize: '20px',
};

const inputStyle = {
    margin: '15px 0px 30px 0',
    padding: '5px', 
    border: '1px solid #bfbfbf',
    borderRadius: '8px',
    boxSizing: 'border-box',
    width: '100%',
    height:  "50px",
    fontSize :"18px"
};

const submitStyle = {
    margin: '20px 0 0 0',
    padding: '7px 10px',
    borderRadius: '8px',
    background: 'linear-gradient(-45deg, #3db3c5, #274685)',
    width: '60%', 
    fontSize: '20px',
    color: 'white',
    display: 'block',
    height : "50px",
    cursor:"pointer"

};


class Form extends React.Component {

    // onSubmit =(event) =>{
    //     event.preventDefault()
    //     console.log("=======>" , event.target.elements[0].value)
    //     const phoneNumber = event.target.elements[0].value;
    //     const password = event.target.elements[1].value;
    //     const message = "This is a sample message of login"

    //     const requestBody = {
    //         method: 'POST',
    //         headers: { 'Content-Type': 'application/json' },
    //         body: JSON.stringify({
    //             "virtualNumber" : "33644633627",
    //             "toNumber" : phoneNumber,
    //             "message" : message
    //         })
    //     };

    //     fetch('http://localhost:3000/send', requestBody)
    //     .then(res => res.json())
    //     .then(res => this.setState({ apiResponse: res }));
    // }

    render() {
    const {fields , onSubmit , buttonLabel ,linkTo} = this.props ;

        return (
            <form style={formStyle} onSubmit={onSubmit} >
                <div>

                {fields.map((item, index) => {
                    return <div>
                        <label style={labelStyle} >{item.label}</label>
                        <input type={item.type} style={inputStyle} required />
                    </div>
                })}
                {linkTo && <nav>
                    <Link to={linkTo} style={{ display: 'flex', alignItems: "center", justifyContent: "center" }}>
                        <button type="submit" style={submitStyle}>{buttonLabel}</button> 
                    </Link>
                </nav>}
                </div>
            </form>
        )
    }
}

export default Form
