import React from "react";
import Form from "../components/Form";

const fields = [
  {
    label: "Full Name",
    type: "text",
  },
  {
    label: "Email Id",
    type: "text",
  },
  {
    label: "Phone Number",
    type: "number",
    name: "Phone",
  },
  {
    label: "Address",
    type: "text",
  },
  {
    label: "Password",
    type: "password",
  },
  {
    label: "Confirm Password",
    type: "password",
  },
];

class Login extends React.Component {
  onSubmit = (event) => {
    event.preventDefault();
    console.log("=======>", event.target.elements[0].value);
    const name = event.target.elements[0].value;
    const emailId = event.target.elements[1].value;
    const phoneNumber = event.target.elements[2].value;
    // const phoneNumber = event.target.elements[0].value;

    // const password = event.target.elements[1].value;
    const message = "You are succesfully registered!";

    const requestBody = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        virtualNumber: "33644633627",
        toNumber: phoneNumber,
        message: message,
      }),
    };

    // if()

    fetch("http://localhost:3000/send", requestBody)
      .then((res) => res.json())
      .then((res) => this.setState({ apiResponse: res }));
  };

  render() {
    return (
      <Form
        fields={fields}
        onSubmit={this.onSubmit}
        buttonLabel={"Register"}
        linkTo={"/success"}
      />
    );
  }
}

export default Login;
