import React from 'react';
import Form from "../components/Form"

const registerFields = [
    {
        label: "Phone Number",
        type: "number",
    }
]

const verifyFields = [
    {
        label: "Enter your 4 digit code",
        type: "number",
    }
]

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            registerResponse : {},
            verifyResponse : {}
        };
      }

      onRegisterSubmit =(event) =>{
        event.preventDefault()
        console.log("=======>" , event.target.elements[0].value)
        const phoneNumber = event.target.elements[0].value;

        const resisterRequest = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                "number" : phoneNumber,
                "brand" : "ICT"
            })
        };       


        fetch('http://localhost:3000/request', resisterRequest)
        .then(res => res.json())
        .then(res => this.setState({ registerResponse: res }));
    }

    onVerifySubmit =(event) =>{
        event.preventDefault()
        const {request_id} = this.state.apiResponse;
        const code = event.target.elements[0].value;

        const verifyRequest = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                "request_id" : request_id,
                "code" : code
            })
        };       


        fetch('http://localhost:3000/check', verifyRequest)
        .then(res => res.json())
        .then(res => this.setState({ apiResponse: res }));
    }

    render() {
        const {status} = this.state.apiResponse;
        return (status === "0" ? 
        <Form  fields={verifyFields} onSubmit={this.onVerifySubmit} buttonLabel={"Verify Me!"} linkTo={"/personal-details"}/> : 
        <Form  fields={registerFields} onSubmit={this.onRegisterSubmit} buttonLabel={"Register"} />
        )
    }
}

export default Login
