import React from 'react';
import Form from "../components/Form"

const registerFields = [
    {
        label: "Phone Number",
        type: "number",
    }
]

const verifyFields = [
    {
        label: "Enter your 4 digit code",
        type: "number",
    }
]

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            registerResponse : {},
            verifyResponse : {}
        };
      }

      onRegisterSubmit =(event) =>{
        event.preventDefault()
        const phoneNumber = event.target.elements[0].value;

        const resisterRequest = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                "number" : phoneNumber,
                "brand" : "ICT"
            })
        };       


        fetch('http://localhost:3000/request', resisterRequest)
        .then(res => res.json())
        .then(res => this.setState({ registerResponse: res }));
    }

    onVerifySubmit =(event) =>{
        event.preventDefault()
        console.log("inside verify registerResponse =====>" ,this.state.registerResponse)
        const {request_id} = this.state.registerResponse;
        const code = event.target.elements[0].value;

        const verifyRequest = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                "request_id" : request_id,
                "code" : code
            })
        };       


        fetch('http://localhost:3000/check', verifyRequest)
        .then(res => res.json())
        .then(res => this.setState({ verifyResponse: res }));
    }

    render() {
        return (

        <Form  fields={registerFields} onSubmit={this.onRegisterSubmit} buttonLabel={"Register"} />
        )
    }
}

export default Login
