import React from "react";
import { Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
// import "react-toastify/dist/ReactToastify.css";

const fields = [
  {
    label: "Full Name",
    type: "text",
  },
];

const formStyle = {
  // margin: 'auto',
  padding: "30px",
  border: "1px solid #c9c9c9",
  borderRadius: "5px",
  background: "#f5f5f5",
  width: "400px",
  display: "block",
  minHeight: "300px",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
};

const labelStyle = {
  // margin: '10px 0 5px 0',
  fontFamily: "Arial, Helvetica, sans-serif",
  fontSize: "20px",
  backgroundColor: "green",
  borderRadius: "8px",
  padding: "25px",
  // minHeight : "50px",
  marginBottom: "20px",
  color: "white",
};

class Success extends React.Component {
  notify = (message) =>
    toast.success(message, {
      position: "bottom-center",
      autoClose: 3000,
      hideProgressBar: true,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
    });

  componentDidMount = () => {
    this.notify(
      "Registration confirmation is sent to your number ending with 1086"
    );
  };

  render() {
    return (
      <div style={formStyle}>
        <div>
          <ToastContainer
            bodyClassName="success-toast"
            style={{ width: "60%" }}
          />
          {/* {this.state.success && this.notify("Success!...")} */}
          <div style={labelStyle}>{"Successfully Registered!"}</div>

          <nav>
            <div style={{ display: "flex", justifyContent: "center" }}>
              <span>{"To login "}</span>
              <span>&nbsp;</span>
              <Link
                to={"/login"}
                style={{
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  textDecoration: "none",
                }}
              >
                <span>{" Click Here"}</span>
              </Link>
            </div>
          </nav>
        </div>
      </div>
    );
  }
}

export default Success;
