import React from 'react';
import Form from "../components/Form"

const fields = [
    {
        label: "Phone Number",
        type: "number",
    }
]

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            apiResponse : {}
        };
      }

    onSubmit =(event) =>{
        event.preventDefault()
        console.log("=======>" , event.target.elements[0].value)
        const phoneNumber = event.target.elements[0].value;

        const requestBody = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                "number" : phoneNumber,
                "brand" : "ICT"
            })
        };       


        fetch('http://localhost:3000/request', requestBody)
        .then(res => res.json())
        .then(res => this.setState({ apiResponse: res }));
    }

    render() {
        return (
            <Form  fields={fields} onSubmit={this.onSubmit} buttonLabel={"Register"} />
        )
    }
}

export default Login
