import React from 'react';
import Form from "../components/Form"

const fields = [
    {
        label: "Enter your 4 digit code",
        type: "number",
    }
]

class Verify extends React.Component {

    onSubmit =(event) =>{
        event.preventDefault()
        const phoneNumber = event.target.elements[0].value;
        const message = "This is a sample message of login"

        const requestBody = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                "virtualNumber" : "33644633627",
                "toNumber" : phoneNumber,
                "message" : message
            })
        };

        fetch('http://localhost:3000/send', requestBody)
        .then(res => res.json())
        .then(res => this.setState({ apiResponse: res }));
    }

    render() {
        return (
            <Form  fields={fields} onSubmit={this.onSubmit} buttonLabel={"Verify Me!"} linkTo={"/personal-details"}/>
        )
    }
}

export default Verify
