import React from 'react';
import Form from "../components/Form"

const fields = [
    {
        label: "Enter your 4 digit code",
        type: "number",
    }
]

class Verify extends React.Component {

    onSubmit =(event) =>{
        event.preventDefault()
        const {request_id} = this.state.registerResponse;
        const code = event.target.elements[0].value;

        const verifyRequest = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                "request_id" : request_id,
                "code" : code
            })
        };       


        fetch('http://localhost:3000/check', verifyRequest)
        .then(res => res.json())
        .then(res => this.setState({ verifyResponse: res }));
    }


    render() {
        return (
            <Form  fields={fields} onSubmit={this.onSubmit} buttonLabel={"Verify Me!"} linkTo={"/personal-details"}/>
        )
    }
}

export default Verify
