import React from 'react';
import Form from "../components/Form"

const registerFields = [
    {
        label: "Phone Number",
        type: "number",
    }
]

const verifyFields = [
    {
        label: "Enter your 4 digit code",
        type: "number",
    }
]

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            registerResponse : {},
            verifyResponse : {}
        };
      }

      onRegisterSubmit =(event) =>{
        event.preventDefault()
        const phoneNumber = event.target.elements[0].value;

        const resisterRequest = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                "number" : phoneNumber,
                "brand" : "ICT"
            })
        };   

        fetch('http://localhost:3000/request', resisterRequest)
        .then(res => res.json())
        .then(res => this.setState({ registerResponse: res }));
    }


    render() {
        return (
            <Form  
                fields={registerFields} 
                onSubmit={this.onRegisterSubmit} 
                buttonLabel={"Register"} 
                linkTo={"/verify"} 
                apiResponse={this.state.registerResponse}/>
        )
    }
}

export default Login
