import React from 'react';

const formStyle = {
    // margin: 'auto',
    padding: '30px',
    border: '1px solid #c9c9c9',
    borderRadius: '5px',
    background: '#f5f5f5',
    width: '400px',
    display: 'block',
    height : '300px'
};

const labelStyle = {
    margin: '10px 0 5px 0',
    fontFamily: 'Arial, Helvetica, sans-serif',
    fontSize: '20px',
};

const inputStyle = {
    margin: '5px 0px 30px 0',
    padding: '5px', 
    border: '1px solid #bfbfbf',
    borderRadius: '8px',
    boxSizing: 'border-box',
    width: '100%',
    height:  "50px",
    fontSize :"18px"
};

const submitStyle = {
    margin: '20px 0 0 0',
    padding: '7px 10px',
    borderRadius: '8px',
    background: 'linear-gradient(-45deg, #3db3c5, #274685)',
    width: '60%', 
    fontSize: '20px',
    color: 'white',
    display: 'block',
    height : "50px",
    cursor:"pointer"

};

const fields = [
    {
        label: "Phone Number",
        type: "number",
    },
    {
        label: "Password",
        type: "password"
    }
]

class Login extends React.Component {

    onSubmit =() =>{

        const requestBody = {
            "virtualNumber" : "33644633627",
            "toNumber" : "33664061086",
            "message" : "Second message for SMS"
        }

        fetch('http://localhost:3000/send', requestBody)
        .then(response => response.json())
        .then(data => this.setState({ postId: data.id }));

        fetch("http://localhost:3000/send")
        .then(res => res.text())
        .then(res => this.setState({ apiResponse: res }));
    }

    render() {
        return (
            <form style={formStyle} onSubmit={this.onSubmit} >
                {fields.map((item, index) => {
                    return <div>
                        <label style={labelStyle} >{item.label}</label>
                        <input type={item.type} style={inputStyle} required />
                    </div>
                })}

                {/* <Field ref={usernameRef} label="Phone:" type="number" />
                <Field ref={passwordRef} label="Password:" type="password" /> */}
                <div style={{ display: 'flex', alignItems: "center", justifyContent: "center" }}>
                    <button style={submitStyle} type="submit">Submit</button>
                </div>
            </form>
        )
    }
}

export default Login
